#Create a new datasource
$config = @{
    name = "MyDatasource"
    # Additional configuration properties
}
New-LMDatasource -Datasource $config

$config = @{
    name = "MyLogicModule"
    # Additional configuration properties
}
New-LMLogicModule -LogicModule $config -Type "datasources"

